package TDWebTest.CaptureScreenShot;

public class patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
	     
	  
	        // outer loop to handle number of rows 
	        //  n in this case 
	        for (int i = 5; i >= 1; i--) 
	        {
	            for (int j = 1; j <= i; j++)
	            {
	                System.out.print(j+" ");
	            }
	             
	            System.out.println();
	        }
	         
	    } 

	}


